    </div>
    <!-- Review Section Create review or read others reviews -->
    <div class="review-section">
      <section class="make-review-section">
        <h2>Add a Review</h2>
        <form @submit.prevent="submitReview">
          <div>
            <label for="name">Name:</label>
            <input v-model="form.name" id="name" required />
          </div>
          <div>
            <label for="rating">Rating (1-5):</label>
            <select v-model.number="form.rating" id="rating" required>
              <option disabled value="">Select rating</option>
              <option v-for="n in 5" :key="n" :value="n">{{ n }} Star</option>
            </select>
          </div>
          <div>
            <label for="content">Review:</label>
            <textarea v-model="form.content" id="content" required></textarea>
          </div>
          <p v-if="successMessage" class="success-message">{{ successMessage }}</p>
          <button type="submit">Submit Review</button>
        </form>
      </section>
      <section class="reviews" v-if="book.reviews && book.reviews.length">
        <h2>Reviews:</h2>
        <ul>
          <li v-for="(review, i) in book.reviews" :key="i">
            <div>
              <p>{{ review.name }}</p>
              <img :src="`/assets/images/rating-star-${review.rating}.jpg`" :alt="`Rating: ${review.rating} stars`" />
            </div>
            <p>{{ review.content }}</p>
          </li>
        </ul>
      </section>
      <!-- If no reviews-->
      <section v-else>
        <p>No reviews yet.</p>
      </section>
    </div>
  </main>
  <!-- If No books-->
  <p v-else>Loading book...</p>
</template>


<style lang="scss" scoped>

main {
  padding-right: 40px;
  padding-left: 40px;
  background-color: $color-background;
  height: 100%;
  width: 100%;
  margin-bottom: 5rem;
  .top-image {
    width: 100%;
    height: 400px;
    right: 0;
    img {
      width: 100%;
      /* Ensure the image fits within the container */
      height: 100%;
      /* Ensure the image fits within the container */
      object-fit: cover;
      object-position: center;
      filter: blur(4px);
    }
  }

  .back-btn-box {
    width: 120px;
    height: 40px;
    margin-left: 40px;

    .button-back {
      display: flex;
      align-items: center;
      text-decoration: none;
      font-family: $font-paragraph;
      color: black;
      svg {
        width: 50px;
        height: 40px;
        transform: scale(0.7) translateX(0);
        transition: transform 0.5s ease-in-out;
      }

      .back-text {
        text-align: center;
        border: none;
        font-size: 1.5rem;
        cursor: pointer;
        height: 100%;
        width: 60px;
      }

      &:hover {
        svg {
          transform: scale(0.7) translateX(-20px);
        }

        .back-text {
          font-weight: bolder;
        }
      }
    }
  }

  .book-section {
    height: auto;
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: center;
    position: relative;
    right: 100px;
    gap: 100px;
    margin-bottom: 20px;
    .owl {
      position: absolute;
      right: 0;
      bottom: 25px;
      path {
        fill: #BABABA;
      }
    }

    &-left {
      width: 325px;
      height: 500px;
      margin-left: 40px;
      border-radius: 16px;
      box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.692);

      img {
        width: 100%;
        height: 100%;
        border-radius: 16px;
      }
    }

    &-right {
      width: 600px;
      height: auto;
      display: flex;
      flex-direction: column;
      gap: 10px;

      ul {
        display: flex;
        flex-direction: row;
        align-items: center;
        gap: 10px;

        :nth-child(2) {
          background-color: #7CFDC0;
        }

        :nth-child(3) {
